      return <div className="itemRow">{listitem.map((item, index) => <div className="itemColumns" key={index}>{Array.isArray(item) ? this.recurTrial(item) : <Item key={index} rownum={this.state.row} columnnum={this.state.column} title={item}></Item>}</div>)}</div>
